CREATE TABLE patient (id BIGINT IDENTITY, name VARCHAR(50) NOT NULL, surname VARCHAR(50) NOT NULL, patronymic VARCHAR(50) NOT NULL, phone VARCHAR(50)  NULL, PRIMARY KEY (id))
CREATE TABLE doctor (id BIGINT IDENTITY, name VARCHAR(50) NOT NULL, surname VARCHAR(50) NOT NULL, patronymic VARCHAR(50) NOT NULL, specialization VARCHAR(200)  NULL, PRIMARY KEY (id))
CREATE TABLE recipe (id BIGINT IDENTITY, description VARCHAR(50) NOT NULL, patient_id BIGINT  NOT NULL, doctor_id BIGINT  NOT NULL, —Åreation_date DATE NOT NULL, expiration_date DATE NOT NULL, priority VARCHAR(50) NOT NULL, PRIMARY KEY (id), FOREIGN KEY (patient_id)  REFERENCES patient ON DELETE RESTRICT, FOREIGN KEY (doctor_id)  REFERENCES doctor ON DELETE RESTRICT)